install_mysql = falseinstall_docker = falselocal system = require("system")local cache = require("cache")local function check()    local processes = system.processes()    for i,v in ipairs(processes) do        if  string.find(v['cmdline'], "mysqld") ~= nil then            install_mysql = true        end        if  string.find(v['cmdline'], "docker") ~= nil then            install_docker = true        end    end    cache.set_global_cache('install_mysql', install_mysql)    cache.set_global_cache('install_docker', install_docker)endcheck()