local function check() local cache_key="current_users" local currents=users() local old=get_cache(cache_key) if not old then  set_cache(cache_key, currents)  return end local content='' local count = 0 for i,v in ipairs(old) do  local exist=false  for ii,vv in ipairs(currents) do   if vv["username"] == v["username"] and vv["uid"] == vv["uid"] then    exist = true    break   end  end  if not exist then   if content ~= '' then content=content.."; " end   count = count + 1   content = content..string.format("%s", v["username"])  end end if content ~= '' then  trigger({Content=content, Num=tostring(count)})  set_cache(cache_key, currents) endendcheck()