local system = require("system")local cache = require("cache")local common = require("common")local sc_file = require("file")local function crontab_add_check(...) local cache_key="crontab_len" local old=cache.get_cache(cache_key) local currents=#system.crontab() if old == nil then  old = system.crontab() end if currents > old then  trigger({Num=tostring(currents - old)}) end cache.set_cache(cache_key, currents)endlocal function check()   local cache_key="crontab_len"   local currents=#system.crontab()   --local old=cache.get_cache(cache_key)   cache.set_cache(cache_key, currents)   local dir = "/var/spool/cron"   while not exit do    if sc_file.file_exist(dir)  then     common.watcher(dir, {1, 2, 4, 8, 16}, crontab_add_check)    end    system.sc_sleep(5)   endendcheck()